$MonitorNames = Get-WmiObject -Namespace root\wmi -ClassName WmiMonitorID | ForEach-Object { if ($_.UserFriendlyName) { [System.Text.Encoding]::ASCII.GetString($_.UserFriendlyName -ne 0) } else { "Generic Monitor" } }; Add-Type -AssemblyName System.Windows.Forms; [System.Windows.Forms.Screen]::AllScreens | ForEach-Object -Begin {$i=1} -Process { "$i`: $($MonitorNames[$i-1]) ($($_.DeviceName))"; $i++ }